import styled from 'styled-components';

interface ISwitchProps {
  disabled?: boolean;
}

export const SwitchContainer = styled.div<ISwitchProps>`
  position: relative;
`;

export const CheckBoxLabel = styled.label`
  position: absolute;
  top: 0;
  transition: 0.3s;
  left: 0;
  border: 2px solid ${(props) => props.theme.colors.secondary.main};
  box-shadow: inset -2px -2px 4px rgba(112, 112, 112, 0.4);
  width: 36px;
  height: 18px;
  background: transparent;
  border-radius: 100px;
  cursor: pointer;
  display: flex;
  &::before {
    content: url('data:image/svg+xml;base64,');
    position: absolute;
    width: 60px;
    height: 60px;
    top: 0px;
    left: 6px;
    transition: 0.3s;
  }
  &::after {
    content: '';
    z-index: 2;
    display: block;
    margin: 2px;
    width: 14px;
    height: 14px;
    background: ${({ theme }) => theme.colors.white};
    box-shadow: 0px 2px 2px rgba(0, 0, 0, 0.25), inset -1px -1px 1px rgba(0, 0, 0, 0.4);
    border-radius: 100px;
    transition: 0.3s;
  }
`;

export const CheckBox = styled.input`
  opacity: 0;
  transition: 0.3s;
  z-index: 1;
  border-radius: 100px;
  width: 36px;
  height: 18px;
  &:not(:checked) + ${CheckBoxLabel} {
    border: 2px solid ${(props) => props.theme.colors.grey.light[5]};
    &::before {
      content: url('data:image/svg+xml;base64,');
      ${({ disabled }) => disabled && 'opacity: 0.3;'};
      display: block;
      border-radius: 100px;
      top: 2px;
      margin-left: 13px;
      transition: 0.3s;
    }
  }
  &:checked + ${CheckBoxLabel} {
    background: ${(props) => props.theme.colors.secondary.main};
    &::after {
      content: '';
      display: block;
      border-radius: 50%;
      width: 14px;
      height: 14px;
      margin-left: 20px;
      transition: 0.3s;
      background: ${(props) => props.theme.colors.grey.main};
    }
  }
`;
